{{
  (async () => {
    // 1. 校验确认框 (使用你指定的组件名)
    if (!checkbox_confirm_approve.isChecked) {
        showAlert('请勾选确认框以继续！', 'warning');
        return; // 终止
    }

    try {
        // 2. 执行核心操作
        await Approve_Booking.run(); // 改状态为 1
        await Log_Approve.run();     // 记日志 'APPROVE'

        // 3. 收尾工作
        closeModal('approve_modal');
        Get_Pending_Bookings.run();  // 刷新表格
        showAlert('审批通过！', 'success');

        // 4. 重置勾选框
        resetWidget('checkbox_confirm_approve'); 

    } catch (error) {
        showAlert('操作失败：' + error.message, 'error');
    }
  })()
}}

