{{
  (async () => {
    // 1. 校验确认框 (使用你指定的组件名)
    if (!checkbox_confirm_approve.isChecked) {
        showAlert('请勾选确认框以继续！', 'warning');
        return; // 终止
    }

    try {
        // 2. 执行核心操作
        await Approve_Booking.run(); // 改状态为 1
        await Log_Approve.run();     // 记日志 'APPROVE'

        // 3. 收尾工作
        closeModal('approve_modal');
        Get_Pending_Bookings.run();  // 刷新表格
        showAlert('审批通过！', 'success');

        // 4. 重置勾选框
        resetWidget('checkbox_confirm_approve'); 

    } catch (error) {
        showAlert('操作失败：' + error.message, 'error');
    }
  })()
}}

{{
  (async () => {
    // 1. 校验确认框 (使用你指定的组件名)
    if (!checkbox_confirm_reject.isChecked) {
        showAlert('请先勾选确认框！', 'warning');
        return; // 终止
    }

    // 2. 校验驳回理由 (防止空理由)
    if (!input_reject_reason.text || input_reject_reason.text.trim() === "") {
        showAlert('请填写驳回原因！', 'warning');
        return; // 终止
    }

    try {
        // 3. 执行核心操作 (顺序执行)
        await Reject_Booking.run();  // 改状态为 2
        await Log_Reject.run();      // 记日志 'REJECT'

        // 4. 收尾工作
        closeModal('reject_modal');       // 关弹窗
        Get_Pending_Bookings.run();       // 刷新待审批表格
        showAlert('已驳回并记录日志', 'success');

        // 5. 清理现场 (重置输入框和勾选框)
        resetWidget('Input_Reject_Reason');
        resetWidget('checkbox_confirm_reject'); 

    } catch (error) {
        // 兜底错误提示
        showAlert('操作失败：' + error.message, 'error');
    }
  })()
}}

{{
  (async () => {
    try {
        // 【关键修改】第一步：先写日志！
        // 趁着它们状态还是 0 的时候，把它们记录下来
        await Log_System_Auto_Cancel.run();

        // 第二步：再执行更新
        // 把状态从 0 改为 2
        const result = await Auto_Cancel_Overdue.run();

        // 第三步：刷新列表
        await Get_Pending_Bookings.run();

        // 反馈结果
        if (result.affectedRows > 0) {
            showAlert(`清理完成！已自动取消 ${result.affectedRows} 条超时预约。`, "success");
        } else {
            showAlert("清理完成，当前没有需要清理的超时预约。", "info");
        }
    } catch (error) {
        showAlert("执行失败：" + error.message, "error");
    }
  })()
}}
