async () => {
  if (!Input_DeptNameUpdate.text.trim()) {
    showAlert('部门名称不能为空！', 'warning');
    return;
  }
  try {
    await Update_Dept.run();
    showAlert('部门修改成功！', 'success');
    closeModal('Modal_CreateDeptCopy');
    await Get_All_Depts.run();
  } catch (error) {
    showAlert('修改失败：' + error.message, 'error');
  }
}

