children.onClick:
{{
  (async () => {
    // 1. 存房间name和id
    await storeValue('target_room_name', currentItem.name);

		await
		storeValue('target_room_id', currentItem.id)
    // 2. 存开始时间 (读取外面筛选组件的值)
    await storeValue('target_start_time', date_start.formattedDate);

    // 3. 存结束时间
    await storeValue('target_end_time', date_end.formattedDate);

    // 4. 最后打开弹窗 (确保数据存好后再打开)
    showModal('booking_modal');
  })()
}}
